mapper.select:
select IFNULL(sum(fire_number),0) as fire_number, IFNULL(sum(fault_number),0) as fault_number, IFNULL(sum(event_number),0) as event_number, IFNULL(sum(hidden_number),0) as hidden_number, IFNULL(sum(hidden_handle_number),0) as hidden_handle_number, IFNULL(sum(fire_handle_in_15m),0) as fire_handle_in_15m, IFNULL(sum(fire_handle_in_1h),0) as fire_handle_in_1h, IFNULL(sum(fire_handle_in_1d),0) as fire_handle_in_1d, IFNULL(sum(fire_handle_after_1d),0) as fire_handle_after_1d, IFNULL(sum(phone_number),0) as phone_number, IFNULL(sum(phone_fail_number),0) as phone_fail_number, IFNULL(sum(message_number),0) as message_number, IFNULL(sum(message_fail_number),0) as message_fail_number, IFNULL(sum(app_push_number),0) as app_push_number, IFNULL(sum(app_push_fail_number),0) as app_push_fail_number from place_statistics INNER JOIN user_place on place_statistics.place_id = user_place.place_id
and place_statistics.project_id = #{projectId,jdbcType=VARCHAR}
and place_statistics.year_month_time = #{yearMonth,jdbcType=INTEGER}
and user_place.user_id = #{userId,jdbcType=VARCHAR}

select IFNULL(sum(fire_number),0) as fire_number, IFNULL(sum(fault_number),0) as fault_number, IFNULL(sum(event_number),0) as event_number, IFNULL(sum(hidden_number),0) as hidden_number, IFNULL(sum(hidden_handle_number),0) as hidden_handle_number, IFNULL(sum(fire_handle_in_15m),0) as fire_handle_in_15m, IFNULL(sum(fire_handle_in_1h),0) as fire_handle_in_1h, IFNULL(sum(fire_handle_in_1d),0) as fire_handle_in_1d, IFNULL(sum(fire_handle_after_1d),0) as fire_handle_after_1d, IFNULL(sum(phone_number),0) as phone_number, IFNULL(sum(phone_fail_number),0) as phone_fail_number, IFNULL(sum(message_number),0) as message_number, IFNULL(sum(message_fail_number),0) as message_fail_number, IFNULL(sum(app_push_number),0) as app_push_number, IFNULL(sum(app_push_fail_number),0) as app_push_fail_number from place_statistics
and place_statistics.project_id = #{projectId,jdbcType=VARCHAR}
and place_statistics.year_month_time = #{yearMonth,jdbcType=INTEGER}

select IFNULL(sum(fire_number),0) as fire_number, IFNULL(sum(fault_number),0) as fault_number, IFNULL(sum(event_number),0) as event_number, IFNULL(sum(hidden_number),0) as hidden_number, IFNULL(sum(hidden_handle_number),0) as hidden_handle_number, IFNULL(sum(fire_handle_in_15m),0) as fire_handle_in_15m, IFNULL(sum(fire_handle_in_1h),0) as fire_handle_in_1h, IFNULL(sum(fire_handle_in_1d),0) as fire_handle_in_1d, IFNULL(sum(fire_handle_after_1d),0) as fire_handle_after_1d, IFNULL(sum(phone_number),0) as phone_number, IFNULL(sum(phone_fail_number),0) as phone_fail_number, IFNULL(sum(message_number),0) as message_number, IFNULL(sum(message_fail_number),0) as message_fail_number, IFNULL(sum(app_push_number),0) as app_push_number, IFNULL(sum(app_push_fail_number),0) as app_push_fail_number from place_statistics as ps INNER JOIN user_project as up on ps.project_id = up.project_id
and ps.project_id = #{projectId,jdbcType=VARCHAR}
and ps.year_month_time = #{yearMonth,jdbcType=INTEGER}
and up.user_id = #{userId,jdbcType=VARCHAR}
